        versionedHashes.push(getVersionedHash(1, commit));
      } else {
        const commit = (0, index_js_3.hexlify)(blob.commitment);
        blobs.push({
          data: (0, index_js_3.hexlify)(blob.data),
          commitment: commit,
          proof: (0, index_js_3.hexlify)(blob.proof)
